mapper.update:
update free_board
set f_title = #{f_title},
	f_content = #{f_content}
where f_idx = #{f_idx} and f_id = #{f_id}

update tip_board
set t_title = #{t_title},
	t_content = #{t_content}
where t_idx = #{t_idx} and t_id = #{t_id}

update comp_board
set c_title = #{c_title},
	c_content = #{c_content}
where c_idx = #{c_idx} and c_id = #{c_id}
