mapper.select:
select * from free_comment
where f_idx = #{f_idx}
order by fc_num

mapper.update:
update free_comment
set fc_comment = #{fc_comment}
where fc_idx = #{fc_idx} and fc_id = #{fc_id}

mapper.select:
select * from tip_comment
where t_idx = #{t_idx}
order by tc_num

mapper.update:
update tip_comment
set tc_comment = #{tc_comment}
where tc_idx = #{tc_idx} and tc_id = #{tc_id}

mapper.select:
select * from comp_comment
where c_idx = #{c_idx}
order by cc_num

mapper.update:
update comp_comment
set cc_comment = #{cc_comment}
where cc_idx = #{cc_idx} and cc_id = #{cc_id}
